DROP TRIGGER IF EXISTS `delreply`;
DELIMITER ;;
CREATE TRIGGER `delreply` AFTER DELETE ON `comment` FOR EACH ROW delete from reply_comment where reply_comment.`comment_id`= old.`id`
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `delcollect`;
DELIMITER ;;
CREATE TRIGGER `delcollect` AFTER DELETE ON `feature` FOR EACH ROW delete from collect where collect.`feature_id`= old.`id`
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `insertRelation`;
DELIMITER ;;
CREATE TRIGGER `insertRelation` AFTER INSERT ON `organization` FOR EACH ROW insert into focus_relation  set focus_relation.`user_id` = new.`user_id`,focus_relation.`class_id` = new.`id`,focus_relation.`power` = 3
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `changeRelation`;
DELIMITER ;;
CREATE TRIGGER `changeRelation` AFTER UPDATE ON `organization` FOR EACH ROW delete from focus_relation where focus_relation.`user_id`= old.`user_id` and focus_relation.`power`=3
;;
DELIMITER ;
DROP TRIGGER IF EXISTS `changeCreate`;
DELIMITER ;;
CREATE TRIGGER `changeCreate` AFTER UPDATE ON `organization` FOR EACH ROW insert into focus_relation  set focus_relation.`user_id` = new.`user_id`,focus_relation.`class_id` = new.`id`,focus_relation.`power` = 3
;;
DELIMITER ;
